
function dumpError(err){
	if (typeof(err) === 'object'){
		if (err.message){
			console.log('\nMessage: ' + err.message);			
		}
		
		if (err.stack){
			console.log('\nStacktrace:');
			console.log('==================================');
			console.log(err.stack);
		}
	}else if (err){
		console.log('dumpError :: ' + err.toString() + '\n');
	}
}

module.exports = dumpError;
