var jsutil = require('jsutil');

var gmlog = function(){};

gmlog.log = function(o){
	var now = new Date();
	var datestr = jsutil.dateToStr(now);
	jsutil.AppendFile("log/gmlog_" + datestr + ".log", jsutil.timeToStr(now.getTime() / 1000) + ' ' + jsutil.tostr(o) + '\r\n');
}

module.exports = gmlog;