var fs = require('fs');
var G = require('global');
var cache = {};

function readfile(name){
	if (G.CONFIG.htmlcache && name in cache)
		return cache[name];
	var path = __dirname + "/public/" + name + ".html";
	var s = fs.readFileSync(path).toString().replace(/resource\//g, '/');
	if (G.CONFIG.htmlcache)
		cache[name] = s;
	return s;
}

module.exports = readfile;